// search - map 표시 
// react-kakao-maps-sdk
import { Map, MapMarker } from "react-kakao-maps-sdk";
import { useState, useEffect, useRef } from 'react';
import style from './Food.module.css';

const Mymap = ({ searchRes }) => {
    // 위치 표시하기 위한 useRef
    const mapRef = useRef({ markers: [] }); //빈 배열로 초기화

    // 마커 표시
    const [desMarkers, setMarkers] = useState([]);

    //바뀌는 마커의 상태를 저장
    const [saveMarker, setSaveMarker] = useState();

    // 위치 표시
    const [state, setState] = useState({
        center:{
            lat : 33.450701,
            lng : 126.570667,
        },
        errMsg: null,
        isLoading: true,
    });

    // 마커의 위치 저장
    const selectMarker = () => {
        setSaveMarker(mapRef.current.value);
    }

    useEffect(() =>{
        if(navigator.geolocation){
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const {latitude, longitude} = position.coords;
                    setState((prev) =>({
                        ...prev,
                        center:{
                            lat : latitude,
                            lng : longitude,
                        },
                        isLoading:false,
                    }));
                },
            (err) => {
                setState((prev)=>({
                    ...prev,
                    errMsg : err.message,
                    isLoading: false,
                }));
            }
                );
        }
        else{
            setState((prev)=>({
                ...prev,
                errMsg: 'geolocation 사용불가',
                isLoading: false
            }));
        }
    },[]);

    // searchRef object에 있는 주소를 좌표로 변환하고 마커 추가
    useEffect(() => {
        //주소-좌표간 변환 서비스 객체를 생성한다.
        const geocoder = new window.kakao.maps.services.Geocoder();

        // 이전 마커를 삭제하기 위해 현재 마커를 변수에 저장
        const prevMarkers = desMarkers

        //searchRes에 있는 각 식당의주소 좌표로 변환하고 마커 표시
        const updateMark = searchRes.map((item) => {
            const fullAddr = item.city + item.city_gu + item.city_dong + item.city_address;
            geocoder.addressSearch(fullAddr, (result, status) => {
                if (status == window.kakao.maps.services.Status.OK) {
                    const coords = new window.kakao.maps.LatLng(result[0].y, result[0].x);
                    const marker = new window.kakao.maps.Marker({
                        position: coords,
                    });
                    marker.setMap(mapRef.current);
                    // mapRef.current.markers.push(marker);
                }
            });
        });

        //마커들을 상태에 저장하고 지도에 추가
        setMarkers(updateMark);
        // updateMark.forEach((marker) => marker.setMap(mapRef.current));

        //이전 마커를 삭제
        // prevMarkers.forEach((marker) => marker.setMap(null));

    }, [searchRes]);

    // 검색결과가 있을 때, 첫 번째 음식점의 좌표를 가져오기
    const getFirstRestaurant = () =>{
        if(searchRes.length > 0){
            return{
                lat : parseFloat(searchRes[0].latitude),
                lng : parseFloat(searchRes[0].longitude)
            }
        }
    }
    console.log(desMarkers);
    return (
        <div className={`${style.kakaoMap}`}>
            <Map ref={mapRef} onChange={selectMarker}// 지도를 표시할 Container
                center={{
                    // 지도의 중심좌표
                    lat: 33.450701,
                    lng: 126.570667,
                }}
                style={{
                    // 지도의 크기
                    width: "650px",
                    height: "550px",
                }}
                level={3} // 지도의 확대 레벨
            >
                <MapMarker position={{
                    lat: 33.450701,
                    lng: 126.570667
                }} />
            </Map>
        </div>
    );
}
export default Mymap;
