// Foodlist ÌÅ¥Î¶≠ÌïòÎ©¥ ÏÉÅÏÑ∏Ï†ïÎ≥¥ Îã¨ÏïÑÏ£ºÍ∏∞(Modal)
import style from './Modal.module.css';
import ReactModal from 'react-modal';
import { useState, useEffect } from 'react';
import { useRecoilState, useRecoilValue } from 'recoil';
import { isLoggedInState, nicknameState, idState } from '../LoginRecoil';
import { Restaurant_id } from '../FoodInfoRecoil';
import axios from 'axios';
import { Link, useLocation } from 'react-router-dom';
import StarRating from './StarRating';

// Îã§Î•∏ Ïª¥Ìè¨ÎÑåÌä∏ÏóêÏÑú Ìï¥Îãπ Ïª¥Ìè¨ÎÑåÌä∏Ïùò Î≥ÄÏàò? Ìï®Ïàò? Ïì∞Î†§Î©¥ props ÏÑ§Ï†ï Ìï¥ÏïºÌï®
const FoodInfo = ({ isOpen, onClose, restaurant, rvLists }) => {
    // Î°úÍ∑∏Ïù∏ ÏÇ¨Ïö©ÏûêÎßå Ïù¥Ïö©ÌïòÍ≤å ÌïòÍ∏∞ ÏúÑÌï® !
    const [isLoggedin, setIsLoggedIn] = useRecoilState(isLoggedInState);

    // dbÎÑ£Í∏∞ ÏúÑÌï¥ÏÑú Î∞õÎäî Í±∞ (nickname, constent, review score)
    const [restId, setRestId] = useRecoilState(Restaurant_id);
    const nickname = useRecoilValue(nicknameState);
    const [reviewScore, setReviewScore] = useState(0);
    //  review ÏÉÅÌÉú
    const [content, setContent] = useState('');

    const [hoverRating, setHoverRating] = useState(0);

    const [id, setId] = useRecoilState(idState);

    //useLocation
    const location = useLocation();
    const from = location.pathname; //ÌòÑÏû¨ ÌéòÏù¥ÏßÄ Í≤ΩÎ°ú Ï†ÄÏû•

    if (!isOpen) return null;
    setRestId(restaurant.id);
    // console.log('restid', restId);

    // # submit !
    const handleSubmit = () => {
        
        const data = {
            restId,
            nickname,
            content,
            reviewScore,
        }

        //review data fetchÌïòÍ∏©
        //review ÏûëÏÑ±Ìï¥ÏÑú Î≤ÑÌäº ÌÅ¥Î¶≠ÌïòÎ©¥ Îç∞Ïù¥ÌÑ∞ Î≥¥ÎÇ¥Í∏∞
        const getData = () => {
            // event.preventDefault();
            let url = 'http://10.125.121.176:8080/insertReview';

            //fetch()
            axios.post(url, data)
                .then((resp) =>
                    // ÏÑúÎ≤Ñ ÏùëÎãµ Ï≤òÎ¶¨ 
                    console.log('Î¶¨Î∑∞ ÏûëÏÑ± ÏÑ±Í≥µ', resp.data))
                .catch((err) => {
                    // Ïò§Î•ò Ï≤òÎ¶¨
                    console.log('Î¶¨Î∑∞ ÏûëÏÑ± Ïò§Î•ò:', err);
                });
        }
        getData();

        //Î¶¨Î∑∞ÏûëÏÑ± ÏôÑÎ£å ÌõÑ Ï¥àÍ∏∞Ìôî
        setContent('');
        setReviewScore(0);

    }
    // console.log('rvlist', rvLists);
    // console.log('nickname', nickname);
    // Î≥ÑÏ†ê ÎßåÎì§Í∏∞
    const handleStarClick = (value) => {
        setReviewScore(value);
    }

    const handleStarHover = (value) => {
        setHoverRating(value);
    }

    // review List Î™®Îã¨Ï∞ΩÏóê ÎùÑÏö∞Í∏∞(DBÏóê nickname, content, score, date)
    let rvTags = [];
    for (let row of rvLists) {

        let icon;
        let score = parseInt(row.reviewScore);
        if (score === 0) {
            icon = 'ü§çü§çü§çü§çü§ç'
        }
        else if (score === 1) {
            icon = <span>
                <img src='./images/star1.svg'/>
                <img src='./images/star2.svg'/>
                <img src='./images/star3.svg'/>
            </span>
        }
        else if (score === 2) {
            icon = '‚ù§‚ù§ü§çü§çü§ç'
        }
        else if (score === 3) {
            icon = '‚ù§‚ù§‚ù§ü§çü§ç'
        }
        else if (score === 4) {
            icon = '‚ù§‚ù§‚ù§‚ù§ü§ç'
        }
        else if (score === 5) {
            icon = '‚ù§‚ù§‚ù§‚ù§‚ù§'
        }

        rvTags.push(
            <tr className={style.RvLists} key={row.id}>
                <td>{row.nickname}</td>
                <td>{row.content}</td>
                <td>{score != null ? icon : 'None'}</td>
                <td>{row.date}</td>
            </tr>
        )
    }

    return (
        // react-modal ÏÇ¨Ïö©Ìï†Îïå ÏöîÏÜå ÏßÄÏ†ï Îã§ Ìï¥Ï£ºÏñ¥Ïïº Ìï®
        <ReactModal
            isOpen={isOpen} // Î™®Îã¨ Ïò§Ìîà
            onRequestClose={onClose} // ÌÅ¥Î°úÏ¶à
            portalClassName={style.modalContent} //Î™®Îã¨ Ï∞Ω Ïä§ÌÉÄÏùº
            overlayClassName={style.modalOverlay} // Î∞∞Í≤Ω Ïä§ÌÉÄÏùº
            shouldCloseOnOverlayClick={false} // Î™®Îã¨ Ï∞Ω Ïô∏Î∂Ä ÌÅ¥Î¶≠ÏúºÎ°ú Îã´ÌûàÎäî Í≤É Î∞©ÏßÄ
        >
            {/* Î™®Îã¨ Ï∞ΩÏùò ÌÅ¨Í∏∞Í∞Ä Î≥ÄÍ≤ΩÎêòÏßÄ ÏïäÎäî Ïù¥Ïú†Í∞Ä react modalÏù¥ Ï∞Ω ÌÅ¨Í∏∞Î•º Ï†úÏñ¥ÌïòÎäî Î∞©ÏãùÍ≥º Í¥ÄÎ†®ÏûàÏùÑ Ïàò ÏûàÏùå ! Í∑∏ÎûòÏÑú ÎÇ¥Î∂ÄÏóêÏÑú ÌÅ¥ÎûòÏä§ ÎÑ§ÏûÑ ÏßÄÏ†ïÌï¥ÏÑú Ï∞Ω ÌÅ¨Í∏∞ Ï°∞Ï†à ; ÏïàÎêå „Ö°„Ö°*/}
            {/* Î™®Îã¨ ÎÇ¥Ïö© Ï∂îÍ∞Ä : Î¶¨Î∑∞, ÌèâÏ†ê Îì± */}
            <div className={style.modalContentInner}>
                <h2 style={{ fontSize: '50px' }}>{restaurant.rname}</h2>
                <h4>{restaurant.city + ' ' + restaurant.city_gu + ' ' + restaurant.city_dong + ' ' + restaurant.city_address}</h4>
                {/* Î°úÍ∑∏Ïù∏ ÏÉÅÌÉúÎ©¥ Î¶¨Î∑∞ Î≥¥Ïù¥Í≤å, ÏïÑÎãàÎ©¥ Î°úÍ∑∏Ïù∏ ÌïòÎèÑÎ°ù ÎßåÎì¶ */}
                {isLoggedin != null ?
                    (<form>
                        <label htmlFor="textarea_field">Î¶¨Î∑∞Î•º ÏûëÏÑ±ÌïòÏÑ∏Ïöî</label>
                        {/* text area : input typeÏùò textÎ≥¥Îã§ Îçî Í∏¥ ÌÖçÏä§Ìä∏Î•º ÏûÖÎ†•Î∞õÏùÑ Ïàò ÏûàÏùå */}
                        <textarea type='text'
                            value={content} onChange={(e) => setContent(e.target.value)} />
                        <div>
                            {/* Î≥ÑÏ†ê ÏûÖÎ†• */}
                            <p>Î≥ÑÏ†êÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî : </p>
                            <StarRating
                                reviewScore={reviewScore}
                                onStarClick={handleStarClick}
                                hoverRating={hoverRating}
                                onStarHover={handleStarHover}
                            />
                            <p> ÏÑ†ÌÉùÌïú Î≥ÑÏ†ê : {reviewScore}</p>
                        </div>
                        {/* Î¶¨Î∑∞Î¶¨Ïä§Ìä∏ Î≥¥Ïó¨Ï£ºÍ∏∞ */}
                        {/*rvLists === []Î°ú ÌñàÏùÑÎïå ÏÇºÌï≠Ïó∞ÏÇ∞Ïûê ÏïûÏóê ÏΩîÎìú Ïã§Ìñâ ÏïàÎêòÎäî Î¨∏Ï†ú : rvListsÍ∞Ä Îπà Î∞∞Ïó¥Ïù∏ Í≤ΩÏö∞ÏóêÎèÑ Îπà ÌÖåÏù¥Î∏îÏù¥ Î†åÎçîÎßÅÎêòÍ≥† ÏûàÏäµÎãàÎã§. Ïù¥Îäî JSXÏùò ÏÇºÌï≠ Ïó∞ÏÇ∞Ïûê ÎÇ¥Î∂ÄÏóêÏÑú Î∞∞Ïó¥Ïùò ÎπÑÏñ¥ÏûàÎäî ÏÉÅÌÉúÎ•º Í≤ÄÏÇ¨ÌïòÎäî Î∞©Ïãù ÎïåÎ¨∏Ïóê Î∞úÏÉùÌïòÎäî Î¨∏Ï†úÏûÖÎãàÎã§.
                        Ïù¥Îü∞ Í≤ΩÏö∞ÏóêÎäî rvListsÏùò Í∏∏Ïù¥Î•º Í≤ÄÏÇ¨ÌïòÏó¨ Î†åÎçîÎßÅ Ïó¨Î∂ÄÎ•º Í≤∞Ï†ïÌïòÎäî Í≤ÉÏù¥ Ï¢ãÏäµÎãàÎã§. */}
                        {rvLists.length === 0 ? <div>
                            <h3>Review</h3>
                            <p>ÏïÑÏßÅ ÏûëÏÑ±Îêú Î¶¨Î∑∞Í∞Ä ÏóÜÏäµÎãàÎã§.</p></div> :
                            <div>
