import { Link, useLocation } from 'react-router-dom';
import { useEffect, useState } from 'react';
import styles from './Community.module.css';
const PostDetail = () => {
    const location = useLocation();
    const { post } = location.state;

    console.log(post);
    
    if (!post) {
        return <div>Loading...</div>;
    }
    
    const handleDeletePost = (reviewId) => {
        // 토큰 기반으로 리뷰 삭제 처리
        let url = 'http://10.125.121.176:8080/deleteReview/';
        url = url + `${reviewId}`;

        // API 호출
        axios.delete(url)
            .then((resp) => {
                console.log('리뷰 삭제 성공', resp.data);

                // 리뷰 삭제한 후 해당 리뷰를 제외한 새 배열 생성하기
                const updateReviews = rvLists.filter((item) => item.id !== reviewId);
                setUserReviews(updateReviews);
            })
            .catch((err) => {
                console.log('리뷰 삭제 오류', err);
            });
    };


    return (
        <div className={styles.commBox}>
            <div className={styles.comm_rec1}>
                <div className={styles.comm_rec2}>
                    <h2>{post.title}</h2>
                    <p>{post.content}</p>
                </div>
                <Link to='/comm'>Return to List</Link>
            </div>
        </div>
    );
};
export default PostDetail;
