// recoil 이용해서 로그인 정보 전역으로 사용 가능하게 함
import { atom, atoms } from 'recoil';

export const idState = atom({
    key: 'idState',
    default: '',
});

export const passwordState = atom({
    key: 'passwordState',
    default: '',
});

export const isLoggeIn = atom({
    key: 'isLoggedIn',
    default: sessionStorage.getItem('jwt'),
});
