import style from './Food.module.css';
// 검색 페이지 
import { Map } from 'react-kakao-maps-sdk';
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
const FoodFind = ({ locations }) => {

    // location의 city, city_gu 빼오기
    const [city, setCity] = useState();
    const [city_gu, setCity_gu] = useState();


    useEffect(() => {

        // kakao map api scr 로드된 후 실행될 콜백함수
        let map;
        const initMap = () => {
            const container = document.getElementById('map');
            const options = {
                // 카카오 맵 초기 중심좌표
                center: new window.kakao.maps.LatLng(37.5665, 126.9780),
                // 카카오 맵 확대 레벨
                level: 3
            };
            map = new window.kakao.maps.Map(container, options);
        }

        //kakao map api 동적 업로딩
        const scr = document.createElement('scr');
        scr.src = '//dapi.kakao.com/v2/maps/sdk.js?appkey=1a66f7df4244f355c405032cae50e0d4';
        scr.onload = initMap();
        document.head.appendChild(scr);

        // component가 언마운트 될 때 scr 제거
        return () => {
            document.head.removeChild(scr);
        };
        
    }, []);

    // # 시/도 선택하기

    // Event Handeler 생성(onChange 걸어줌)
    // 시/도 가 선택되면 그 타겟을 잡음
    const checkCity = (event) => {
        setCity(event.target.value); // 선택된 시/도의 value 값을 잡음
        setCity_gu(''); // 구를 선택할때마다 기존 선택값 초기화
    }

    // 구 가 선택되면 그 타겟을 잡음
    const checkCity_gu = (event) => {
        setCity_gu(event.target.value); // 선택된 구의 value 값 업데이트
    }

    // #-1. category 중복제거
    const uniqueCities = Array.from(new Set(locations.map(item => item.city)));

    // #-2. category map 돌려서 하나씩 가져옴
    let cate = uniqueCities.map((item) =>
        <option key={item.city + item.nubmer} value={item.rname}>
            {item}
        </option>
    );

    // # 구 선택
    // #-1. 상위 카테고리에서 설정해서 가져오기
    let cate_gu = locations.filter(item => item.city === city) // 선택된 시/도와 동일한 '구' 필터링
        .map(item => (
            <option key={item.city + item.number} value={item.rname}>
                {item.city_gu}
            </option>
        ));

    return (
        <div className='container'>
            <div className={`${style.dropDown}`}>
                {/* select '광역시', '구' */}
                <div className={`${style.label}`}>
                    <label for="error_select">광역시/도를 선택하세요</label>
                    <div className='nes-select is-error' >
                        <select required id="error_select" onChange={checkCity()}>
                            <option defaultValue='' disabled selected hidden>Select...</option>
                            {cate}
                        </select>
                    </div>
                </div>
                <div className={`${style.label}`}>
                    <label for="error_select">구를 선택하세요</label>
                    <div className="nes-select is-error">
                        <select required id="error_select">
                            <option defaultValue="" disabled selected hidden onChange={checkCity_gu()}>Select...</option>
                            {cate_gu}
                        </select>
                    </div>
                </div>
                <div className={`${style.label}`}>
                    <label for="textarea_field">Textarea</label>
                    <textarea id="textarea_field" className={`nes-textarea ${style.inputTxt}`}></textarea>
                </div>
            </div>
            {/* 지도  */}
            <div id='map' className={`${style.kakaoMap}`}></div>
        </div>
    );
}
export default FoodFind;
