// 게시판 페이지
import { Link } from 'react-router-dom';
import { useState } from 'react';
// community 상단에는 login / join us 구현하기

const FoodComm = () => {

    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLogin = () =>{
        setIsLoggedIn(true);
    }

    const handleLogout = () => { 
        setIsLoggedIn(false);
    }

    return (
        <div>
            <Link to={'/joinus'}>Join us</Link>
            {isLoggedIn ? <Link to={'/login'} onChange={handleLogin}>Login</Link> : <Link to={'/logout'} onChange={handleLogout}>Logout</Link>}
        </div>
    );
}
export default FoodComm;
