// search - map 표시 
// react-kakao-maps-sdk
import { CustomOverlayMap, Map, MapMarker } from "react-kakao-maps-sdk";
import { useState, useEffect, useRef } from 'react';
import style from '../Food.module.css';
import FoodInfo from "./FoodInfo";

const Mymap = ({ searchRes }) => {

    // 위치 표시하기 위한 useRef
    const mapRef = useRef({ markers: [] }); //빈 배열로 초기화
    // 이전 마커를 저장
    const markersRef = useRef([]);

    // 마커 표시
    const [desMarkers, setMarkers] = useState([]);

    // custom overlay 표시
    const [isOpen, setIsOpen] = useState(false);

    // 초기 위치 표시
    const [state, setState] = useState({
        center: {
            lat: 33.450701,
            lng: 126.570667,
        },
        errMsg: null,
        isLoading: true,
    });

    // searchRef object에 있는 주소를 좌표로 변환하고 마커 추가
    useEffect(() => {
        //주소-좌표간 변환 서비스 객체를 생성한다.
        const geocoder = new window.kakao.maps.services.Geocoder();

        //이전 마커 저장
        markersRef.current.forEach(marker => marker.setMap(null));

        //searchRes에 있는 각 식당의주소 좌표로 변환하고 마커 표시
        const updateMark = searchRes.map((item) => {
            const fullAddr = item.city + item.city_gu + item.city_dong + item.city_address;
            geocoder.addressSearch(fullAddr, (result, status) => {
                if (status == window.kakao.maps.services.Status.OK) {
                    const coords = new window.kakao.maps.LatLng(result[0].y, result[0].x);
                    const marker = new window.kakao.maps.Marker({
                        position: coords,
                    });
                    marker.setMap(mapRef.current);
                    // 이전 마커를 배열에 저장
                    markersRef.current.push(marker);
                    // mapRef.current.markers.push(marker);
                }
            });
        });

        //마커들을 상태에 저장하고 지도에 추가
        setMarkers(updateMark);
        // updateMark.forEach((marker) => marker.setMap(saveMarker));

        //이전 마커를 삭제
        // prevMarkers.forEach((marker) => marker.setMap(null));

        // 검색결과가 있을 때, 첫 번째 음식점의 좌표를 가져오기
        if (searchRes.length > 0) {
            const firstAddr = searchRes[0].city + searchRes[0].city_gu + searchRes[0].city_dong + searchRes[0].city_address;
            geocoder.addressSearch(firstAddr, (result, status) => {
                if (status === window.kakao.maps.services.Status.OK) {
                    const coords = new window.kakao.maps.LatLng(result[0].y, result[0].x);

                    // 마커를 클릭했을 때 이벤트 핸들러 추가(이거 문제 있음 미췬)
                    // window.kakao.maps.event.addListener(marker, 'click', () => {
                    setState((prev) => ({
                        ...prev,
                        center: {
                            lat: coords.getLat(),
                            lng: coords.getLng(),
                        },
                    }));
                    // });
                }
            });
        }
    }, [searchRes]);
    console.log(isOpen);

    // 아래와 같이 커스텀 오버레이 열기/닫기를 제어할 수 있습니다.
    const handleOverlayToggle = () => {
        setIsOpen(!isOpen);
    };

    // overlay 창에 표시할 정보
    let title;
    title = searchRes.map((item) => item.rname);
    // console.log('title', title);
    // console.log(desMarkers);
    return (
        <div className={`${style.kakaoMap}`}>

            <Map ref={mapRef} // 지도를 표시할 Container
                center={state.center}
                style={{
                    // 지도의 크기
                    width: "550px",
                    height: "450px",
                    display: "flex",
                }}
                level={2} // 지도의 확대 레벨
            >
                <MapMarker position={{ lat: state.center?.lat, lng: state.center?.lng }} onClick={handleOverlayToggle} />
                {isOpen && (
                    <CustomOverlayMap position={{ lat: state.center?.lat, lng: state.center?.lng }}>
                        <div className={`${style.overlay} wrap`}>
                            <div className="info">
                                <div className="title">
                                    {title}
                                    <div
                                        className="close"
                                        onClick={() => setIsOpen(false)}
                                        title="닫기"></div>
                                </div>
                                <div className="body">
                                    <div className="img">
                                        <img src="//t1.daumcdn.net/thumb/C84x76/?fname=http://t1.daumcdn.net/cfile/2170353A51B82DE005"
                                            width='70'
                                            height='70' />
                                    </div>
                                    <div className="desc">
                                        <div className="ellipsis">
                                            {searchRes.map((item) => item.city + item.city_gu + item.city_dong + item.city_address)}
                                        </div>
                                        <div>
                                            <a
                                                href="https://www.kakaocorp.com/main"
                                                target="_blank"
                                                className="link"
                                                rel="noreferrer"
                                            >
                                                홈페이지
                                            </a>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </CustomOverlayMap>
                )}
            </Map>
        </div>
    );
}
export default Mymap;
