import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useRecoilState } from 'recoil';
import { isLoggedInState } from '../LoginRecoil';
import styles from './Community.module.css';
import style from './Login.module.css';

const FoodComm = () => {
    // Pagination
    const [currentPage, setCurrentPage] = useState(1);
    const postsPerPage = 10;

    const [isLoggedIn, setIsLoggedIn] = useRecoilState(isLoggedInState);
    const [bList, setBList] = useState([]);
    const [listTags, setListTags] = useState([]);

    const navigate = useNavigate();

    // ... (handleLogin, handleLogout, useEffect 등)

    // ... (clickPage, useEffect 등)

    const handleWriting = () => {
        if (!isLoggedIn) {
            alert('로그인이 필요합니다.');
        } else {
            navigate('/write');
        }
    };

    const renderPages = () => {
        const totalPages = Math.ceil(bList.totalElements / postsPerPage);
        const pages = [];

        for (let i = 1; i <= totalPages; i++) {
            pages.push(
                <span
                    key={i}
                    className={currentPage === i ? styles.currentPage : styles.page}
                    onClick={() => handleClick(i)}
                >
                    {i}
                </span>
            );
        }

        return (
            <div className={styles.paging}>
                {pages}
            </div>
        );
    };

    return (
        <main>
            <div>
                <Link to={'/joinus'}>Join us</Link>
                {isLoggedIn ? (
                    <button type='button' className={`${style.logoutBtn}`} onClick={handleLogout}>
                        Logout
                    </button>
                ) : (
                    <Link to={'/login'} onClick={handleLogin}>Login</Link>
                )}
            </div>

            <span className={styles.star1}>
                <img src='./images/comm_back1.png' />
            </span>
            <div className={styles.commBox}>
                <div className={styles.comm_rec1}>
                    <div>
                        <table className={styles.comm_rec2}>
                            <thead>
                                <tr>
                                    <th>No</th>
                                    <th>Title</th>
                                    <th>Date</th>
                                    <th>Nickname</th>
                                </tr>
                            </thead>
                            <tbody>
                                {listTags}
                            </tbody>
                        </table>
                        <button className={styles.writeBtn} onClick={handleWriting}>
                            Write
                        </button>
                        {renderPages()}
                    </div>
                </div>
                <div>
                    <span>
                        <img className={styles.star2} src='./images/comm_back2.svg' />
                    </span>
                    <img className={styles.hearts} src='./images/hearts.svg' />
                </div>
            </div>
        </main>
    );
};

export default FoodComm;
